import React, { Component, useState } from "react";
import AppImage1 from "../../img/pickup.svg";
import AppImage2 from "../../img/bike.svg";
import AppImage3 from "../../img/taxi.svg";

function Applications() {
  return (
    <section className="w-[100%] bg-[#F2F5F8] py-[40px]">
      <div className="w-full px-[6%]">
        <ApplicationTitle />
        <Application />
      </div>
    </section>
  );
}

class ApplicationTitle extends Component {
  constructor() {
    super();
    this.state = {
      Text: "یک اپلیکیشن، برای تمام نیازها",
    };
  }
  render() {
    return (
      <section className="flex justify-center w-full">
        <h2 className="text-[23px] md:text-[29px] lg:text-[33px] xl:text-[36px] font-semibold text-slate-700">
          {this.state.Text}
        </h2>
      </section>
    );
  }
}

function Application() {
  const [data, setdata] = useState([
    {
      Image: AppImage1,
      Title: "درخواست وانت",
      Desc: "حمل و نقل با اسنپ",
    },
    {
      Image: AppImage2,
      Title: "پیک موتوری",
      Desc: "حمل و نقل با اسنپ",
    },
    {
      Image: AppImage3,
      Title: "تاکسی اینترنتی",
      Desc: "امکان درخواست آنلاین خودرو با اسنپ",
    },
  ]);
  return (
    <section className="w-full flex flex-wrap justify-center border border-red-600">
      {data.map((val, index) => {
        return <ApplicationItems key={index} {...val} />;
      })}
    </section>
  );
}

function ApplicationItems({ Image, Title, Desc }) {
  return (
    <div className="xl:w-[29%] p-4 flex justify-between items-center">
      <article className="w-2/5 flex items-center justify-between bg-white">
        <figure className="w-2/12">
          <img src={Image} alt="" />
        </figure>
        <section className="grow">
          <h4>{Title}</h4>
          <p>{Desc}</p>
        </section>
      </article>
    </div>
  );
}

export default Applications;
