import React from "react";
import Img from "./../../img/intro_desktop.jpg";

function Motto() {
  return (
    <section className="w-full mt-[5rem]">
      <div className="w-full flex flex-wrap items-center ">
        <TextMotto />
        <ImgMotto />
      </div>
    </section>
  );
}

function ImgMotto() {
  return (
    <figure className="h-full w-full xl:w-[50%]">
      <img src={Img} className="h-full w-full object-cover" alt="ImgMotto" />
    </figure>
  );
}

function TextMotto() {
  return (
    <section className="h-full w-full xl:w-[50%] bg-[#21aa58] py-[7.5rem] px-[6%]">
      <h1 className="text-white mb-[16px] text-[36px]">
        تجربه&zwnj;ی زندگی راحت&zwnj;تر، سریع&zwnj;تر و به&zwnj;صرفه&zwnj;تر با
        سوپراپلیکیشن اسنپ!
      </h1>
      <p className="text-white text-[20px]">
        از درخواست خودرو گرفته تا سفارش غذا، خرید سوپرمارکتی، خرید بلیط سفر،
        رزرو هتل و... را میتوانید با اسنپ انجام دهید.
      </p>
    </section>
  );
}

export default Motto;
